import NavigationBar from "@/components/Navigation";
import { biller, execute, parking, rent, trade } from "@/api";
export default {
  name: "LiveMonitoring",
  components: {
    NavigationBar
  },
  data() {
    return {
      users: [],
      message: null
    };
  },
  mounted() {
    this.getUsersBySubCounty;
  },
  methods: {
    formatPrice(value) {
      var formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'KES',
        minimumFractionDigits: 2
      });
      return formatter.format(value);
    },
    getUsersBySubCounty() {
      const data = new FormData();
      data.append("function", "getUsersBySubCounty");
      data.append("subCountyID", "43246");
      execute(data, biller).then(res => {
        if (res.data.success) {
          this.users = res.data.data.users;
        } else {
          this.message = res.data.message;
        }
      }).catch(e => {
        //alert(e.message);
        this.message = e.message;
      });
    }
  }
};
