import NavigationBar from "@/components/Navigation";
import ToolBar from "@/components/sbp/ToolBar";
export default {
  name: "sbpDashboard",
  components: {
    ToolBar,
    NavigationBar
  },
  mounted() {
    this.activeVsExpired();
    this.revenuePerformance();
    this.applicationStatus();
  },
  methods: {
    applicationStatus() {
      // JavaScript code inside the <script> tag
      document.addEventListener('DOMContentLoaded', function () {
        // Generate random values for the categories
        const randomValues = {
          initiated: Math.floor(Math.random() * 100),
          pendingVerification: Math.floor(Math.random() * 100),
          pendingInspection: Math.floor(Math.random() * 100),
          pendingCertification: Math.floor(Math.random() * 100)
        };

        // Create the half donut chart with links and custom font
        Highcharts.chart('application-statuses', {
          chart: {
            type: 'pie',
            style: {
              fontFamily: 'Montserrat' // Set font family
            }
          },

          title: {
            text: null
          },
          plotOptions: {
            pie: {
              innerSize: '50%',
              // Make it a half donut
              depth: 45,
              // Increase the depth for a 3D effect
              dataLabels: {
                style: {
                  fontFamily: 'Montserrat' // Set font family
                }
              }
            }
          },

          series: [{
            name: 'Applications',
            data: [{
              name: 'Initiated',
              y: randomValues.initiated,
              link: 'https://example.com/initiated'
            }, {
              name: 'Pending Verification',
              y: randomValues.pendingVerification,
              link: 'https://example.com/pending-verification'
            }, {
              name: 'Pending Inspection',
              y: randomValues.pendingInspection,
              link: 'https://example.com/pending-inspection'
            }, {
              name: 'Pending Certification',
              y: randomValues.pendingCertification,
              link: 'https://example.com/pending-certification'
            }],
            point: {
              events: {
                click: function () {
                  // Open the link when the slice is clicked
                  window.open(this.link, '_blank');
                }
              }
            }
          }]
        });
      });
    },
    revenuePerformance() {
      // Your chart generation code will go here
      document.addEventListener('DOMContentLoaded', function () {
        // Abbreviate months to three letters
        function abbreviateMonth(month) {
          return month.substring(0, 3);
        }

        // Prepare the data
        const data = [['Jul 2023', 50000, 60000, 55000],
        // [Month, Last Year, Target, Actual]
        ['Aug 2023', 55000, 62000, 58000], ['Sep 2023', 52000, 61000, 56000], ['Oct 2023', 53000, 63000, 57000], ['Nov 2023', 54000, 59000, 56000], ['Dec 2023', 60000, 65000, 62000], ['Jan 2024', 58000, 67000, 59000], ['Feb 2024', 59000, 68000, 60000], ['Mar 2024', 61000, 70000, 62000], ['Apr 2024', 60000, 69000, 61000], ['May 2024', 62000, 72000, 63000], ['Jun 2024', 63000, 71000, 64000]];

        // Set the font family to Montserrat
        Highcharts.setOptions({
          chart: {
            style: {
              fontFamily: 'Montserrat, sans-serif'
            }
          }
        });

        // Create the bar chart
        Highcharts.chart('revenuePerfomance', {
          chart: {
            type: 'column'
          },
          title: {
            text: null
          },
          xAxis: {
            categories: data.map(item => abbreviateMonth(item[0]))
          },
          yAxis: {
            title: {
              text: 'Revenue (KES)'
            }
          },
          legend: {
            enabled: true
          },
          tooltip: {
            shared: true,
            pointFormat: '<span style="color:{point.color}">\u25CF</span> {series.name}: <b>KES {point.y}</b><br/>'
          },
          plotOptions: {
            bar: {
              pointWidth: 20,
              // Adjust the width of each column
              stacking: 'normal',
              // Prevent stacking
              borderWidth: 0
            }
          },
          series: [{
            name: 'Last Year',
            data: data.map(item => item[1])
          }, {
            name: 'Target',
            data: data.map(item => item[2])
          }, {
            name: 'This Year',
            data: data.map(item => item[3])
          }]
        });
      });
    },
    activeVsExpired() {
      Highcharts.setOptions({
        chart: {
          style: {
            fontFamily: 'Montserrat, sans-serif'
          }
        },
        labels: {
          style: {
            fontSize: '14px' // Adjust label font size
          }
        }
      });
      // Your chart generation code will go here
      document.addEventListener('DOMContentLoaded', function () {
        // Create the pie chart
        var pieColors = ['#FF0000', '#00FF00']; // Red for expired, Green for active

        Highcharts.chart('certificateStatus', {
          chart: {
            type: 'pie'
          },
          title: {
            text: null
          },
          plotOptions: {
            pie: {
              allowPointSelect: true,
              cursor: 'pointer',
              dataLabels: {
                enabled: true,
                format: '<b>{point.name}</b>: {point.percentage:.1f} %'
              }
            }
          },
          series: [{
            name: 'All SBP Certificates',
            colorByPoint: true,
            colors: pieColors,
            data: [{
              name: 'Expired',
              y: 30,
              drilldown: 'expired'
            }, {
              name: 'Active',
              y: 70,
              drilldown: 'active'
            }]
          }],
          drilldown: {
            series: [{
              name: 'Expired Certificates',
              id: 'expired',
              data: [['Homabay Town', 10], ['Rachuonyo North', 5], ['Suba North', 3], ['Rachuonyo South', 4], ['Rachuonyo East', 8], ['Ndhiwa', 6], ['Suba South', 2], ['Rangwe', 7], ['Homa Bay Hq', 5]]
            }, {
              name: 'Active Certificates',
              id: 'active',
              data: [['Homabay Town', 50], ['Rachuonyo North', 40], ['Suba North', 30], ['Rachuonyo South', 45], ['Rachuonyo East', 35], ['Ndhiwa', 25], ['Suba South', 20], ['Rangwe', 30], ['Homa Bay Hq', 60]]
            }]
          }
        });
      });
    }
  }
};
